import React, { useState, useRef } from 'react';
import {
  View,
  Text,
  StyleSheet,
  Modal,
  TouchableOpacity,
  Dimensions,
  Platform,
  Alert,
  ActivityIndicator,
  Image,
  ScrollView,
  SafeAreaView,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import QRCode from 'react-native-qrcode-svg';
import ViewShot from 'react-native-view-shot';
import * as Sharing from 'expo-sharing';
import * as FileSystem from 'expo-file-system';
import { resourceURLs, getResourceURL } from '../../config/apiConfig';

interface PosterModalProps {
  visible: boolean;
  onClose: () => void;
  title: string;
  category: string;
  summary: string;
  articleUrl: string;
  onShowMessage?: (type: 'success' | 'error' | 'warning' | 'info', title: string, message: string) => void;
}

const { width, height } = Dimensions.get('window');

const PosterModal: React.FC<PosterModalProps> = ({
  visible,
  onClose,
  title,
  category,
  summary,
  articleUrl,
  onShowMessage,
}) => {
  const [isGenerating, setIsGenerating] = useState(false);
  const [logoError, setLogoError] = useState(false);
  const viewShotRef = useRef<ViewShot>(null);

  // 安全的文本处理函数，清理可能导致问题的字符
  const sanitizeText = (text: string) => {
    if (!text) return '';
    return text
      .replace(/[\u200B-\u200D\uFEFF]/g, '') // 移除零宽字符
      .replace(/[\u2028\u2029]/g, '\n') // 替换行分隔符
      .replace(/\r\n/g, '\n') // 统一换行符
      .replace(/\r/g, '\n')
      .trim();
  };

  // 安全处理后的参数
  const safeTitle = sanitizeText(title || '') || '无标题';
  const safeCategory = sanitizeText(category || '') || '资讯';
  const safeSummary = sanitizeText(summary || '') || '暂无摘要';
  const safeArticleUrl = articleUrl || '';
  const shareUrl = safeArticleUrl;

  // 您的比特币logo的base64编码
  const customLogoBase64 = 'data:image/png;base64,';

  // 创建一个简单的base64 logo作为备份（使用比特币风格的logo）
  const createFallbackLogo = () => {
    // 如果有自定义logo，直接使用
    if (customLogoBase64) {
      return customLogoBase64;
    }
    
    // React Native环境下返回空字符串，使用文字代替
    return '';
  };

  const fallbackLogoUrl = createFallbackLogo();

  const handleShare = async () => {
    if (!viewShotRef.current) return;

    setIsGenerating(true);
    try {
      // 使用 ViewShot 捕获海报
      const uri = await viewShotRef.current.capture({
        format: 'png',
        quality: 0.9,
        width: 700,
        height: 760,
      });

      if (Platform.OS === 'web' && typeof document !== 'undefined') {
        // Web 端直接下载图片
        const link = document.createElement('a');
        link.href = uri;
        link.download = `chainalert-${Date.now()}.png`;
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
      } else {
        // 移动端使用 Expo Sharing
        if (await Sharing.isAvailableAsync()) {
          await Sharing.shareAsync(uri, {
            mimeType: 'image/png',
            dialogTitle: '分享文章海报',
          });
        } else {
          Alert.alert('提示', '当前设备不支持分享功能');
        }
      }
      
      onClose();
    } catch (error) {
      console.error('生成海报失败:', error);
      Alert.alert('错误', '生成海报失败，请重试');
    } finally {
      setIsGenerating(false);
    }
  };

  const handleDirectShare = async () => {
    try {
      if (Platform.OS === 'web') {
        // Web 端复制链接到剪贴板
        await navigator.clipboard.writeText(shareUrl);
        Alert.alert('成功', '链接已复制到剪贴板');
      } else {
        // 移动端使用系统分享
        const { Share } = require('react-native');
        await Share.share({
          message: `${title} - ChainAlert\n\n${summary}\n\n${shareUrl}`,
          url: shareUrl,
        });
      }
    } catch (error) {
      console.error('分享失败:', error);
    }
  };

  return (
    <Modal
      visible={visible}
      transparent
      animationType="slide"
      onRequestClose={onClose}
    >
      <View style={styles.modalOverlay}>
        <SafeAreaView style={styles.modalContainer}>
          {/* Header */}
          <View style={styles.modalHeader}>
            <Text style={styles.modalTitle}>分享文章</Text>
            <TouchableOpacity onPress={onClose} style={styles.closeButton}>
              <Ionicons name="close" size={24} color="#333" />
            </TouchableOpacity>
          </View>

          {/* Scrollable Content */}
          <ScrollView 
            style={styles.scrollContainer}
            contentContainerStyle={styles.scrollContent}
            showsVerticalScrollIndicator={false}
            bounces={false}
            keyboardShouldPersistTaps="handled"
          >
            {/* Poster Preview */}
            <ViewShot
              ref={viewShotRef}
              style={styles.posterContainer}
              options={{
                format: 'png',
                quality: 0.9,
              }}
            >
              <View style={styles.poster}>
                {/* 品牌头部背景 */}
                <View style={styles.brandHeaderBackground}>
                  <View style={styles.brandHeader}>
                    {fallbackLogoUrl ? (
                      <View style={styles.brandLogoContainer}>
                        <Image 
                          source={{ uri: fallbackLogoUrl }} 
                          style={styles.brandLogo}
                          resizeMode="contain"
                          onError={() => setLogoError(true)}
                        />
                        {!logoError && <Text style={styles.brandNameWithLogo}>小目标</Text>}
                      </View>
                    ) : null}
                    {logoError || !fallbackLogoUrl ? (
                      <Text style={styles.brandName}>小目标</Text>
                    ) : null}
                    <Text style={styles.brandTagline}>专业的区块链资讯平台</Text>
                  </View>
                </View>

                {/* Content */}
                <View style={styles.contentArea}>
                  <View style={styles.categoryBadge}>
                    <Text style={styles.categoryText}>{safeCategory}</Text>
                  </View>
                  
                  <Text style={styles.posterTitle} numberOfLines={2}>
                    {safeTitle}
                  </Text>
                  
                  <Text style={styles.posterSummary} numberOfLines={6}>
                    {safeSummary}
                  </Text>
                  
                  {/* 底部信息区域 */}
                  <View style={styles.bottomSection}>
                    <View style={styles.leftBottomInfo}>
                      <Text style={styles.timestampText}>
                        {new Date().toLocaleDateString('zh-CN')}
                      </Text>
                      <Text style={styles.sourceText}>来源：小目标</Text>
                    </View>
                    
                    <View style={styles.qrContainer}>
                      <QRCode
                        value={shareUrl}
                        size={45}
                        backgroundColor="white"
                        color="#333"
                        logo={fallbackLogoUrl && !logoError ? { uri: fallbackLogoUrl } : undefined}
                        logoSize={fallbackLogoUrl && !logoError ? 8 : 0}
                        logoBackgroundColor="white"
                        logoBorderRadius={4}
                      />
                      <Text style={styles.qrText}>扫码查看</Text>
                    </View>
                  </View>
                </View>
              </View>
            </ViewShot>
          </ScrollView>

          {/* Action Buttons - 固定在底部，确保在安卓设备上始终可见 */}
          <View style={styles.actionButtons}>
            <TouchableOpacity
              style={[styles.actionButton, styles.directShareButton]}
              onPress={handleDirectShare}
            >
              <Ionicons name="share-outline" size={18} color="#007AFF" />
              <Text style={styles.directShareText}>直接分享</Text>
            </TouchableOpacity>
            
            <TouchableOpacity
              style={[styles.actionButton, styles.posterShareButton]}
              onPress={handleShare}
              disabled={isGenerating}
            >
              {isGenerating ? (
                <ActivityIndicator size="small" color="white" />
              ) : (
                <Ionicons name="image-outline" size={18} color="white" />
              )}
              <Text style={styles.posterShareText}>
                {isGenerating ? '生成中...' : '生成海报'}
              </Text>
            </TouchableOpacity>
          </View>
        </SafeAreaView>
      </View>
    </Modal>
  );
};

